import './detail.less'
import React from 'react'
import config from '@/config'
import { util } from '@/utils'
import { PageLoading } from '@/components'
import { Row, Col, Typography, Image, Button, Progress, Space, Tooltip, Statistic } from 'antd'
import {
  TagsOutlined,
  StarOutlined,
  LikeOutlined,
  HeartOutlined,
  CalendarOutlined,
  FieldTimeOutlined,
} from '@ant-design/icons'

export default function Detail({ loading, data, onBack }) {
  const labelGenres = data && data.genres ? data.genres.map(genre => genre.name).join(', ') : 'Unknow Genre'
  const labelProductions =
    data && data.production_companies
      ? data.production_companies.map(item => item.name).join(', ')
      : 'Unknow Productions'
  const labelLanguages =
    data && data.spoken_languages ? data.spoken_languages.map(item => item.name).join(', ') : 'Unknow Languages'

  return loading ? (
    <PageLoading />
  ) : (
    <div className="wrap-background">
      {data && data.backdrop_path && (
        <img alt="poster" className="bg" src={`${config.api.image}/w1920_and_h800_multi_faces${data.backdrop_path}`} />
      )}

      <div style={{ width: '100%', backgroundSize: 'cover', color: 'white' }}>
        <Row gutter={16}>
          <Col xl={6} style={{ marginBottom: 16 }}>
            <Image
              height={490}
              width={'100%'}
              style={{ borderRadius: 16 }}
              src={data && data.poster_path ? `${config.api.image}/w1280${data.poster_path}` : null}
              fallback="data:image/png;base64,"
            />
          </Col>

          <Col xl={18}>
            <Typography.Title>{data && data.original_title}</Typography.Title>
            <Typography.Text type="secondary">
              <Space>
                <label>
                  <CalendarOutlined /> {data && data.release_date ? data.release_date : 'Unknow Release Date'}
                </label>
                <label>
                  <StarOutlined /> {data && data.popularity ? data.popularity : 'Unknow Popularity'}
                </label>
                <label>
                  <TagsOutlined /> {data && labelGenres}
                </label>
                <label>
                  <FieldTimeOutlined /> {data && data.runtime ? util.runtimeConvertion(data.runtime) : 'Unknow Runtime'}
                </label>
              </Space>
            </Typography.Text>

            <Typography.Title level={4}>
              <Space>
                <Tooltip title="Click for loving this movie">
                  <Button type="dashed" shape="circle" size="large" icon={<HeartOutlined />} danger />
                </Tooltip>
                <Progress
                  width={40}
                  type="circle"
                  strokeWidth={10}
                  percent={data && data.vote_average ? parseFloat(data.vote_average * 10) : 0}
                />
                <Statistic value={data && data.vote_count} prefix={<LikeOutlined />} />
              </Space>
            </Typography.Title>

            <Typography.Title level={4}>Overview</Typography.Title>
            <Typography.Paragraph>{data && data.overview}</Typography.Paragraph>

            <Typography.Title level={4}>Productions</Typography.Title>
            <Typography.Text>{data && labelProductions}</Typography.Text>

            <Typography.Title level={4}>Original Languages</Typography.Title>
            <Typography.Text>{data && labelLanguages}</Typography.Text>

            <Typography.Title level={4}>Revenue</Typography.Title>
            <Typography.Text>{data && data.revenue && util.convertToDollar(data.revenue)}</Typography.Text>

            <Typography.Paragraph style={{ marginTop: 24 }}>
              <Button type="primary" onClick={onBack}>
                Back To Homepage
              </Button>
            </Typography.Paragraph>
          </Col>
        </Row>
      </div>
    </div>
  )
}
